package week8day1;

public class SwitchStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number =32
				;
	String size ;
	
	switch(number)
	
	{
	case 29:
		size = "Small";
		break;
	case 30:
		size = "Medium";
		break;
		
	case 32:
		size = "Large";
		break;
		
	default:
		size ="unknown";
		break;
	}
	
	System.out.println("the size is"+size);


		
		

		

	}

}
